InvokeScript:
$RazerPath = "C:\Windows\Installer\Razer"

if (Test-Path $RazerPath) {
  Remove-Item $RazerPath\* -Recurse -Force -ErrorAction SilentlyContinue
}
else {
  New-Item -Path $RazerPath -ItemType Directory -Force -ErrorAction SilentlyContinue | Out-Null
}

icacls $RazerPath /deny "Everyone:(W)" 2>&1 | Out-Null

# Run Disk Cleanup silently using SAGERUN
# First, set up cleanup profile 99 with all options
$cleanupPath = 'HKLM:\SOFTWARE\Microsoft\Windows\CurrentVersion\Explorer\VolumeCaches'
if (Test-Path $cleanupPath) {
  Get-ChildItem $cleanupPath | ForEach-Object {
    Set-ItemProperty -Path $_.PSPath -Name 'StateFlags0099' -Value 2 -Type DWord -ErrorAction SilentlyContinue
  }
}
# Run cleanup with profile 99 (runs in background without prompts)
Start-Process -FilePath 'cleanmgr.exe' -ArgumentList '/SAGERUN:99' -Wait -WindowStyle Hidden
# Run DISM cleanup silently
Start-Process -FilePath 'Dism.exe' -ArgumentList '/online /Cleanup-Image /StartComponentCleanup /ResetBase /Quiet' -Wait -WindowStyle Hidden
